    \draw (S_1_1) -- (S_1_2);

    \draw (S_1_1) arc[start angle=180, end angle=0, radius=1.54]
    node[midway,above] {};

    \node[above=0.5cm] at (7,1.2) {$S_1$};

    % S_2 
    \coordinate (S_2_1) at (6.32,4.82);
